mapper.insert:
insert into user_attention(
user_id,
user_attention_id
) values (
#{userId},
#{userAttentionId}
)

mapper.select:
select * from user_attention
where user_id=#{userId}
and user_attention_id=#{userAttentionId}

mapper.delete:
delete from
user_attention
where
user_id=#{userId}
and
user_attention_id=#{userAttentionId}
